<template>
  <div
    class="Loading"
    :class="[_type]"></div>
</template>

<script>
export default {
  name: 'Loading',

  props: [
    'type',
  ],

  computed: {
    _type() {
      return {
        'Loading--top': this.type === 'top',
        'Loading--relative': this.type === 'relative',
        'Loading--left': this.type === 'left',
      }
    },
  },
}
</script>

<style scoped>
.Loading {
  background-image: url(data:image/svg+xml;base64,);
  background-repeat: no-repeat;
  background-size: 100% 100%;
  animation-name: rotate-loading;
  animation-duration: 550ms;
  animation-timing-function: linear;
  animation-iteration-count: infinite;
  position: fixed;
  width: 20px;
  height: 20px;
  margin-top: -10px;
  margin-left: -10px;
  top: 50%;
  left: 50%;
  z-index: 10;
}

.Loading--relative {
  position: relative;
  margin: 20px -10px 0;
}

.Loading--left {
  position: relative;
  margin: 20px 0 0;
  left: auto;
}

.Loading--top {
  position: fixed;
  top: 20px;
  right: 20px;
  left: auto;
  margin: 0;
}

.is-loaded .Loading {
  display: none;
}

@keyframes rotate-loading {
  0% {
    transform:rotate(0);
  }
  100% {
    transform:rotate(360deg);
  }
}
</style>
